mapper.select:
select distinct
from sysmenu
order by ${orderByClause}

mapper.update:
update sysmenu
Name = #{name,jdbcType=VARCHAR}, ParentId = #{parentid,jdbcType=VARCHAR}, Url = #{url,jdbcType=VARCHAR}, Iconic = #{iconic,jdbcType=VARCHAR}, Sort = #{sort,jdbcType=INTEGER}, State = #{state,jdbcType=VARCHAR}, CreatePerson = #{createperson,jdbcType=VARCHAR}, CreateTime = #{createtime,jdbcType=TIMESTAMP}, UpdateTime = #{updatetime,jdbcType=TIMESTAMP}, UpdatePerson = #{updateperson,jdbcType=VARCHAR}, IsLeaf = #{isleaf,jdbcType=VARCHAR}, TabId = #{tabid,jdbcType=VARCHAR}, CascadeId = #{cascadeid,jdbcType=VARCHAR}, ParentName = #{parentname,jdbcType=VARCHAR}, Remark = #{remark,jdbcType=LONGVARCHAR},
where Id = #{id,jdbcType=VARCHAR}
