mapper.select:
select distinct
from sysoperation
order by ${orderByClause}

mapper.update:
update sysoperation
Name = #{name,jdbcType=VARCHAR}, Script = #{script,jdbcType=VARCHAR}, Icon = #{icon,jdbcType=VARCHAR}, Sort = #{sort,jdbcType=INTEGER}, State = #{state,jdbcType=VARCHAR}, CreateTime = #{createtime,jdbcType=TIMESTAMP}, CreatePerson = #{createperson,jdbcType=VARCHAR}, DomId = #{domid,jdbcType=VARCHAR}, Type = #{type,jdbcType=VARCHAR}, Attr = #{attr,jdbcType=VARCHAR}, ClassCss = #{classcss,jdbcType=VARCHAR}, Remark = #{remark,jdbcType=LONGVARCHAR},
where Id = #{id,jdbcType=VARCHAR}

update sysoperation set Name = #{name,jdbcType=VARCHAR}, Script = #{script,jdbcType=VARCHAR},
Icon = #{icon,jdbcType=VARCHAR}, Sort = #{sort,jdbcType=INTEGER}, State = #{state,jdbcType=VARCHAR},
CreateTime = #{createtime,jdbcType=TIMESTAMP}, CreatePerson = #{createperson,jdbcType=VARCHAR},
DomId = #{domid,jdbcType=VARCHAR}, Type = #{type,jdbcType=VARCHAR}, Attr = #{attr,jdbcType=VARCHAR},
ClassCss = #{classcss,jdbcType=VARCHAR}, Remark = #{remark,jdbcType=LONGVARCHAR}
where Id = #{id,jdbcType=VARCHAR}

mapper.delete:
		delete from Sysoperation
 	where id in
		#{id}
